import { SHAPES } from "@/helpers/game.constants";

// return a random number within a range
export function random(min, max) {
  return Math.random() * (max - min) + min;
}

export function formatShapeIcon(shape) {
  switch (shape) {
    case STOCK_CALCULATION_METHODS.TOTAL_STOCKS:
      return t("message.totalUnits");
    case STOCK_CALCULATION_METHODS.REMAINING_STOCKS:
      return t("message.remainingUnits");
    default:
      return "";
  }
}
