import * as PIXI from "pixi.js";
import Orb from "../helpers/Orb";
export default {
  name: "SweetBackground",
  components: {},
  methods: {},

  mounted() {
    // Create PixiJS app
    const app = new PIXI.Application({
      // render to <canvas class="orb-canvas"></canvas>
      view: document.querySelector(".orb-canvas"),
      // auto adjust size to fit the current window
      resizeTo: window,
      // transparent background, we will be creating a gradient background later using CSS
      backgroundAlpha: 0
    }); // Create orbs

    const orbs = [];

    for (let i = 0; i < 10; i++) {
      // each orb will be black, just for now
      const orb = new Orb(0x000000);
      app.stage.addChild(orb.graphics);
      orbs.push(orb);
    }

    if (!window.matchMedia("(prefers-reduced-motion: reduce)").matches) {
      app.ticker.add(() => {
        // update and render each orb, each frame. app.ticker attempts to run at 60fps
        orbs.forEach(orb => {
          orb.update();
          orb.render();
        });
      });
    } else {
      // perform one update and render per orb, do not animate
      orbs.forEach(orb => {
        orb.update();
        orb.render();
      });
    }
  }

};
