export default class FlapText {
  constructor(element, alphabet) {
    this.element = element;
    this.alphabet = alphabet;
    this.currentPositions = [];
    this.targetPositions = [];
    this.randomPositions = [];
  }

  setInitialPositions(text) {
    const textArray = text.split("");
    const textLength = textArray.length;
    textArray.forEach((char, index) => {
      const charIndex = this.alphabet.indexOf(char);
      if (charIndex === -1) {
        this.alphabet.push(char);
        this.targetPositions[index] = this.alphabet.length - 1;
      } else {
        this.targetPositions[index] = charIndex;
      }
    });
    this.currentPositions = Array(textLength).fill(0);
    this.randomPositions = Array(textLength).fill(0);
  }

  resetCurrentPositions() {
    for (let i = 0; i < this.currentPositions.length; i++) {
      this.currentPositions[i] = this.targetPositions[i];
    }
    this.randomPositions.fill(0);
  }

  shufflePositions() {
    const randomOffset = Math.floor(10 * Math.random()) + 5;
    for (let i = 0; i < this.currentPositions.length; i++) {
      this.currentPositions[i] =
        (this.currentPositions[i] + this.alphabet.length - randomOffset) %
        this.alphabet.length;
      this.randomPositions[i] = Math.floor(10 * Math.random());
    }
  }

  shiftRandomly(offset, multiplier) {
    for (let i = 0; i < this.randomPositions.length; i++) {
      this.randomPositions[i] = offset + i * multiplier;
    }
  }

  flap() {
    if (this.element.dataset.paused !== "false") {
      return;
    }
    console.log("THIS RANDOM POSITIONS", this.randomPositions);
    this.currentPositions.forEach((position, index) => {
      if (this.randomPositions[index] > 0) {
        this.randomPositions[index]--;
      } else if (this.currentPositions[index] !== this.targetPositions[index]) {
        this.currentPositions[index] =
          (this.currentPositions[index] + 1) % this.alphabet.length;
      }
    });
  }

  //   render() {
  //     const newText = this.currentPositions.reduce((text, position) => {
  //       let char = this.alphabet[this.currentPositions[position]];
  //       if (char === ">") {
  //         char = "&gt;";
  //       } else if (char === "<") {
  //         char = "&lt;";
  //       }
  //       return text + char;
  //     }, "");
  //     if (this.element.innerHTML !== newText) {
  //       this.element.innerHTML = newText;
  //     }
  //   }
  render() {
    console.log("innerHTML", this.element.innerHTML);
    if (this.element.innerHTML === "") {
      this.element.innerHTML = this.element.dataset.originalText;
    } else {
      const newText = this.currentPositions.reduce((text, position) => {
        let char = this.alphabet[this.currentPositions[position]];
        console.log("char", char);
        if (!char) return text;
        if (char === ">") {
          char = "&gt;";
        } else if (char === "<") {
          char = "&lt;";
        }
        return text + char;
      }, "");
      console.log(newText);
      if (this.element.innerHTML !== newText) {
        this.element.innerHTML = newText;
      }
    }
  }
}
