const state = {
  projects: [
    {
      name: "project1",
      title: "SIDE PROJECT MANAGMENT APP",
      subtitle: "The side project subtitle",
      image: null,
      tags: [],
    },
    {
      name: "project2",
      title: "PEN PLOTTING PROJECT",
      subtitle: "The side project subtitle",
      image: null,
      tags: [],
    },
    {
      name: "project3",
      title: "NICE MUSIC SHIT",
      subtitle: "The side project subtitle",
      image: null,
      tags: [],
    },
    {
      name: "project4",
      title: "ANOTHER SWEET PROJECT",
      subtitle: "The side project subtitle",
      image: null,
      tags: [],
    },
    {
      name: "project5",
      title: "??? (WORK IN PROGRESS)",
      subtitle: "The side project subtitle",
      image: null,
      tags: [],
    },
  ],
};

const getters = {
  getProjectByName: (state) => (projectName) => {
    return state.projects.find((project) => project.name === projectName)[0];
  },
};

export default {
  namespaced: true,
  state,
  getters,
};
