import utils from "./utils";
class Orb {
  constructor(originX, originY, fillColor) {
    this.bounds = this.setBounds(originX, originY);
    this.x = utils.random(this.bounds["x"].min, this.bounds["x"].max);
    this.y = utils.random(this.bounds["y"].min, this.bounds["y"].max);

    window.addEventListener(
      "resize",
      debounce(() => {
        this.bounds = this.setBounds();
      }, 250)
    );
  }

  setBounds(originX, originY) {
    const maxDist =
      window.innerWidth < 1000 ? window.innerWidth / 3 : window.innerWidth / 5;

    return {
      x: {
        min: originX - maxDist,
        max: originX + maxDist,
      },
      y: {
        min: originY - maxDist,
        max: originY + maxDist,
      },
    };
  }

  move() {
    this.x += 0;
  }

  render() {
    fill(fillColor);
  }
}
