import FlapText from "./helpers/FlapText";
import AppBase from "./layout/AppBase.vue";
export default {
  name: "App",
  components: {
    AppBase,
    SweetBackground
  },
  methods: {
    initFlapText() {
      const characters = "\"'“”‘’¹²³!#$&%()*+,-./0123456789:;<=>?@ABCDEFGHIJKLMNOPQRSTUVWXYZ[\\]^_`abcdefghijklmnopqrstuvwxyz{|}~½¼¡«»×▓▒░│┤▀└┴┬├─┼┐┌└┘".split("");
      const shuffledCharacters = characters.sort((a, b) => 0.5 - Math.random());
      const flapTextElements = [];
      document.querySelectorAll(".flap").forEach(flapElement => {
        const lines = flapElement.innerText.trimEnd().split("\n").map(line => line.trimEnd().replaceAll("\t", ""));
        console.log("LINEs", lines);
        flapElement.innerHTML = "";
        lines.forEach(line => {
          const span = document.createElement("span");
          span.dataset.paused = "true";
          flapElement.appendChild(span);
          new IntersectionObserver(entries => {
            if (entries[0].isIntersecting === true) {
              span.dataset.paused = "false";
            }
          }, {
            threshold: [0]
          }).observe(span);
          const flapText = new FlapText(span, shuffledCharacters);
          flapText.setInitialPositions(line);
          flapText.shufflePositions();
          flapText.render();
          span.addEventListener("mouseenter", () => {
            flapText.shufflePositions();
          });
          flapTextElements.push(flapText);
        });
      });
      let frameCount = 0;
      requestAnimationFrame(function animate() {
        requestAnimationFrame(animate);

        if (frameCount++ % 2 === 0) {
          for (const flapText of flapTextElements) {
            flapText.flap();
            flapText.render();
          }
        }
      });
    }

  }
};
