import { debounce } from "debounce";
import { random, map } from "./utils";

export default class Orb {
  constructor(originX, originY, maxDist, fillColor) {
    console.log("CONSTRUCT");
    this.bounds = this.setBounds();
    this.maxDist = maxDist;
    this.x = random(this.bounds["x"].min, this.bounds["x"].max);
    this.y = random(this.bounds["y"].min, this.bounds["y"].max);

    this.scale = 1;

    this.radius = random(window.innerHeight / 6, window.innerHeight / 3);

    this.xOff = random(0, 1000);
    this.yOff = random(0, 1000);
    this.inc = 0.002;

    this.fillColor = fillColor;
    window.addEventListener(
      "resize",
      debounce(() => {
        this.bounds = this.setBounds();
      }, 250)
    );
  }

  setBounds() {
    const maxDistX = window.innerWidth * this.maxDist;
    const maxDistY = window.innerHeight * this.maxDist;

    return {
      x: {
        min: this.originX - maxDistX,
        max: this.originX + maxDistX,
      },
      y: {
        min: this.originY - maxDistY,
        max: this.originY + maxDistY,
      },
    };
  }

  move(sketchInstance) {
    const xNoise = sketchInstance.noise(this.xOff, this.xOff);
    const yNoise = sketchInstance.noise(this.yOff, this.yOff);
    const scaleNoise = sketchInstance.noise(this.xOff, this.yOff);

    this.x = map(xNoise, -1, 1, this.bounds["x"].min, this.bounds["x"].max);
    this.y = map(yNoise, -1, 1, this.bounds["y"].min, this.bounds["y"].max);
    this.scale = map(scaleNoise, -1, 1, 0.5, 1);

    this.xOff += this.inc;
    this.yOff += this.inc;
  }

  render(sketchInstance) {
    sketchInstance.fill(this.fillColor);
    sketchInstance.ellipse(this.x, this.y, this.radius, this.radius);
  }
}
