import SweetBackground from "./components/SweetBackground.vue";
import { gsap, ScrollTrigger } from "./gsap";
const $html = document.documentElement;
const IS_MOBILE = /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) || navigator.platform === "MacIntel" && navigator.maxTouchPoints > 1;
export default {
  name: "App",
  components: {
    SweetBackground
  },
  methods: {
    // this is the helper function that sets it all up. Pass in the content <div> and then the wrapping viewport <div> (can be the elements or selector text). It also sets the default "scroller" to the content so you don't have to do that on all your ScrollTriggers.
    smoothScroll() {
      const smoothness = 1;
      gsap.set(this.$refs.scroll, {
        overflow: "hidden",
        position: "fixed",
        height: "100%",
        width: "100%",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0
      });
      gsap.set(this.$refs.scrollInner, {
        overflow: "visible",
        width: "100%"
      });
      const getProp = gsap.getProperty(this.$refs.scrollInner);
      const setProp = gsap.quickSetter(this.$refs.scrollInner, "y", "px");
      const setScroll = ScrollTrigger.getScrollFunc(window);

      const removeScroll = () => this.$refs.scrollInner.style.overflow = "visible";

      const killScrub = trigger => {
        let scrub = trigger.getTween();
        scrub && scrub.kill();
        trigger.animation.progress(trigger.progress);
      };

      let isProxyScrolling;

      const refreshSmoothScroll = () => {
        this.scrollHeight = this.$refs.scrollInner.clientHeight;
        this.$refs.scrollInner.style.overflow = "visible";
        $html.style.height = this.scrollHeight + "px";
      };

      refreshSmoothScroll();
      ScrollTrigger.addEventListener("refreshInit", refreshSmoothScroll.bind(this));
      ScrollTrigger.addEventListener("refresh", () => {
        removeScroll();
        requestAnimationFrame(removeScroll);
      });
      ScrollTrigger.defaults({
        scroller: this.$refs.scrollInner
      });
      ScrollTrigger.scrollerProxy(this.$refs.scrollInner, {
        scrollTop(value) {
          if (arguments.length) {
            isProxyScrolling = true; // otherwise, if snapping was applied (or anything that attempted to SET the scroll proxy's scroll position), we'd set the scroll here which would then (on the next tick) update the content tween/ScrollTrigger which would try to smoothly animate to that new value, thus the scrub tween would impede the progress. So we use this flag to respond accordingly in the ScrollTrigger's onUpdate and effectively force the scrub to its end immediately.

            setProp(-value);
            setScroll(value);
            return;
          }

          return -getProp("y");
        },

        getBoundingClientRect() {
          return {
            top: 0,
            left: 0,
            width: window.innerWidth,
            height: window.innerHeight
          };
        }

      });
      return ScrollTrigger.create({
        animation: gsap.fromTo(this.$refs.scrollInner, {
          y: 0
        }, {
          y: () => document.documentElement.clientHeight - this.scrollHeight,
          ease: "none",
          onUpdate: ScrollTrigger.update
        }),
        scroller: window,
        invalidateOnRefresh: true,
        start: 0,
        end: () => this.scrollHeight - document.documentElement.clientHeight,
        scrub: smoothness,
        onUpdate: self => {
          if (isProxyScrolling) {
            killScrub(self);
            isProxyScrolling = false;
          }
        },
        onRefresh: killScrub // when the screen resizes, we just want the animation to immediately go to the appropriate spot rather than animating there, so basically kill the scrub.

      });
    }

  },

  mounted() {
    this.scroller = IS_MOBILE ? false : this.smoothScroll(); // Watch body height change

    let timeout;
    const resizeObserver = new ResizeObserver(() => {
      if (timeout) {
        clearTimeout(timeout);
      }

      timeout = setTimeout(() => {
        ScrollTrigger.refresh();
      }, 200);
    });
    resizeObserver.observe(this.$refs.main);
  }

};
