import { debounce } from "debounce";
import utils from "./utils";

class Orb {
  constructor(originX, originY, fillColor) {
    this.bounds = this.setBounds(originX, originY);
    this.x = utils.random(this.bounds["x"].min, this.bounds["x"].max);
    this.y = utils.random(this.bounds["y"].min, this.bounds["y"].max);

    this.scale = 1;

    this.radius = utils.random(window.innerHeight / 6, window.innerHeight / 3);

    this.xOff = utils.random(0, 1000);
    this.yOff = utils.random(0, 1000);
    this.inc = 0.002;

    this.fillColor = fillColor;
    window.addEventListener(
      "resize",
      debounce(() => {
        this.bounds = this.setBounds();
      }, 250)
    );
  }

  setBounds(originX, originY) {
    const maxDist =
      window.innerWidth < 1000 ? window.innerWidth / 3 : window.innerWidth / 5;

    return {
      x: {
        min: originX - maxDist,
        max: originX + maxDist,
      },
      y: {
        min: originY - maxDist,
        max: originY + maxDist,
      },
    };
  }

  move(sketchInstance) {
    const xNoise = sketchInstance.noise(this.xOff, this.xOff);
    const yNoise = sketchInstance.noise(this.yOff, this.yOff);
    const scaleNoise = sketchInstance.noise(this.xOff, this.yOff);

    this.x = utils.map(
      xNoise,
      -1,
      1,
      this.bounds["x"].min,
      this.bounds["x"].max
    );
    this.y = utils.map(
      yNoise,
      -1,
      1,
      this.bounds["y"].min,
      this.bounds["y"].max
    );
    this.scale = utils.map(scaleNoise, -1, 1, 0.5, 1);

    this.xOff += this.inc;
    this.yOff += this.inc;
  }

  render(sketchInstance) {
    sketchInstance.fill(this.fillColor);
    sketchInstance.ellipse(this.x, this.y, this.radius, this.radius);
  }
}
